import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiUsers, FiDollarSign, FiTrendingUp, FiSettings, FiBell, FiMessageSquare } from 'react-icons/fi';
import DashboardLayout from './DashboardLayout';
import TransactionsTab from './TransactionsTab';
import BBPSServices from './BBPSServices';
import AddMoneyModal from './AddMoneyModal';
import { apiService } from '../../services/apiService';
import { toast } from 'react-toastify';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalTransactions: 0,
    totalRevenue: 0,
    pendingQueries: 0
  });
  const [users, setUsers] = useState([]);
  const [showAddMoney, setShowAddMoney] = useState(false);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [usersResponse] = await Promise.all([
        apiService.getAllUsers()
      ]);

      setUsers(usersResponse.data);
      setStats(prev => ({
        ...prev,
        totalUsers: usersResponse.data.length
      }));
    } catch (error) {
      toast.error('Error fetching dashboard data');
    }
  };

  const handleResetCommission = async () => {
    try {
      await apiService.updateDefaultCommission({
        adminCommissionRate: 2.0,
        parentCommissionRate: 1.0
      });
      toast.success('Default commission rates reset successfully');
    } catch (error) {
      toast.error('Error resetting commission rates');
    }
  };

  const renderDashboard = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <div className="dashboard-grid">
        <motion.div 
          className="dashboard-card clickable-card"
          onClick={() => setActiveTab('users')}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <div className="card-header">
            <div>
              <div className="card-title">Total Users</div>
              <div className="card-value">{stats.totalUsers}</div>
              <div className="card-subtitle">Click to view all users</div>
            </div>
            <FiUsers className="card-icon" />
          </div>
        </motion.div>

        <motion.div 
          className="dashboard-card clickable-card"
          onClick={() => setActiveTab('transactions')}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <div className="card-header">
            <div>
              <div className="card-title">Total Transactions</div>
              <div className="card-value">{stats.totalTransactions}</div>
              <div className="card-subtitle">Click to view all transactions</div>
            </div>
            <FiDollarSign className="card-icon" />
          </div>
        </motion.div>

        <motion.div 
          className="dashboard-card clickable-card"
          onClick={() => setActiveTab('transaction-stats')}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <div className="card-header">
            <div>
              <div className="card-title">Total Revenue</div>
              <div className="card-value">â‚¹{stats.totalRevenue}</div>
              <div className="card-subtitle">Click to view stats</div>
            </div>
            <FiTrendingUp className="card-icon" />
          </div>
        </motion.div>

        <div className="dashboard-card">
          <div className="card-header">
            <div>
              <div className="card-title">Pending Queries</div>
              <div className="card-value">{stats.pendingQueries}</div>
              <div className="card-subtitle">Need attention</div>
            </div>
            <FiMessageSquare className="card-icon" />
          </div>
        </div>
      </div>

      <div className="dashboard-actions">
        <motion.button
          className="action-btn"
          onClick={handleResetCommission}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <FiSettings />
          Reset Default Commission
        </motion.button>
      </div>
    </motion.div>
  );

  const renderUsers = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="transactions-container"
    >
      <div className="transactions-header">
        <h2>All Users</h2>
      </div>
      <table className="transactions-table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Phone</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>
                <span className="status-badge status-completed">
                  {user.role}
                </span>
              </td>
              <td>{user.phone}</td>
              <td>
                <span className="status-badge status-completed">
                  Active
                </span>
              </td>
              <td>
                <button className="action-btn-small">Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </motion.div>
  );

  const renderNotifications = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="transactions-container"
    >
      <div className="transactions-header">
        <h2>Notifications</h2>
      </div>
      <div className="notification-list">
        <div className="notification-item">
          <FiBell className="notification-icon" />
          <div>
            <h4>New User Registration</h4>
            <p>John Doe registered as Retailer</p>
            <small>2 hours ago</small>
          </div>
        </div>
      </div>
    </motion.div>
  );

  const renderQueries = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="transactions-container"
    >
      <div className="transactions-header">
        <h2>User Queries</h2>
      </div>
      <div className="query-list">
        <div className="query-item">
          <div>
            <h4>Payment Issue</h4>
            <p>User reported payment failure</p>
            <small>Status: Pending</small>
          </div>
          <button className="action-btn-small">Resolve</button>
        </div>
      </div>
    </motion.div>
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return renderDashboard();
      case 'users':
        return renderUsers();
      case 'transactions':
        return <TransactionsTab isAdmin={true} />;
      case 'bbps':
        return <BBPSServices />;
      case 'notifications':
        return renderNotifications();
      case 'queries':
        return renderQueries();
      case 'add-money':
        setShowAddMoney(true);
        setActiveTab('dashboard');
        return renderDashboard();
      default:
        return renderDashboard();
    }
  };

  return (
    <DashboardLayout 
      activeTab={activeTab} 
      setActiveTab={setActiveTab} 
      role="ADMIN"
    >
      {renderContent()}
      {showAddMoney && (
        <AddMoneyModal 
          onClose={() => setShowAddMoney(false)}
          onSuccess={() => {
            setShowAddMoney(false);
            toast.success('Money added successfully!');
          }}
        />
      )}
    </DashboardLayout>
  );
};

export default AdminDashboard;
