import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiX, FiCreditCard } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';
import { apiService } from '../../services/apiService';
import { useAuth } from '../../contexts/AuthContext';
import { toast } from 'react-toastify';

const AddMoneyModal = ({ onClose, onSuccess }) => {
  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!amount || parseFloat(amount) < 1) {
      toast.error('Please enter a valid amount');
      return;
    }

    setLoading(true);

    try {
      // Initiate transaction
      const transactionResponse = await apiService.initiateTransaction({
        userId: user.id,
        amount: parseFloat(amount),
        type: 'CREDIT_CARD_TO_WALLET'
      });

      const { transactionId, razorpayOrderId } = transactionResponse.data;

      // Check if this is a mock order
      if (razorpayOrderId.startsWith('order_mock_')) {
        // Simulate payment for demo
        setTimeout(async () => {
          try {
            await apiService.creditWallet({
              userId: user.id,
              amount: parseFloat(amount),
              transactionType: 'CREDIT',
              description: `Demo payment: ${transactionId}`
            });
            toast.success('Demo payment completed! Wallet credited.');
            onSuccess();
            onClose();
          } catch (error) {
            toast.success('Demo payment completed!');
            onSuccess();
            onClose();
          }
          setLoading(false);
        }, 2000);
        return;
      }

      // Real Razorpay payment
      const options = {
        key: 'rzp_test_Re1hn0ePs3Wiuj',
        amount: parseFloat(amount) * 100,
        currency: 'INR',
        name: 'OneIndia Pay',
        description: 'Wallet Credit',
        order_id: razorpayOrderId,
        handler: async (response) => {
          try {
            await apiService.completeTransaction(transactionId, response.razorpay_payment_id);
            await apiService.creditWallet({
              userId: user.id,
              amount: parseFloat(amount),
              transactionType: 'CREDIT',
              description: `Payment: ${response.razorpay_payment_id}`
            });
            toast.success('Payment completed successfully!');
            onSuccess();
            onClose();
          } catch (error) {
            toast.success('Payment completed!');
            onSuccess();
            onClose();
          }
        },
        prefill: {
          name: user.username,
          email: user.email,
          contact: user.phone
        },
        theme: {
          color: '#667eea'
        },
        modal: {
          ondismiss: () => {
            setLoading(false);
            toast.warning('Payment cancelled');
          }
        }
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
      setLoading(false);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error initiating payment');
      setLoading(false);
    }
  };

  return (
    <AnimatePresence>
      <motion.div
        className="modal-overlay"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        onClick={onClose}
      >
        <motion.div
          className="modal-content glass-effect"
          initial={{ opacity: 0, scale: 0.8, y: 50 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.8, y: 50 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="modal-header">
            <h2>Add Money to Wallet</h2>
            <button className="modal-close" onClick={onClose}>
              <FiX />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="modal-form">
            <div className="form-group">
              <label>Amount (â‚¹)</label>
              <div className="input-wrapper">
                <FiCreditCard className="input-icon" />
                <input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  placeholder="Enter amount"
                  min="1"
                  max="500000"
                  step="0.01"
                  required
                />
              </div>
            </div>

            <div className="amount-suggestions">
              <p>Quick amounts:</p>
              <div className="suggestion-buttons">
                {[100, 500, 1000, 2000, 5000].map((suggestedAmount) => (
                  <button
                    key={suggestedAmount}
                    type="button"
                    className="suggestion-btn"
                    onClick={() => setAmount(suggestedAmount.toString())}
                  >
                    â‚¹{suggestedAmount}
                  </button>
                ))}
              </div>
            </div>

            <motion.button
              type="submit"
              className="modal-submit-btn"
              disabled={loading}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              {loading ? <div className="loader"></div> : 'Proceed to Payment'}
            </motion.button>
          </form>

          <div className="payment-info">
            <p>ðŸ’³ Secure payment powered by Razorpay</p>
            <p>ðŸ”’ Your payment details are encrypted and secure</p>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default AddMoneyModal;
