import axios from 'axios';

const createApiService = (baseURL) => {
  const api = axios.create({
    baseURL,
    headers: {
      'Content-Type': 'application/json',
    },
  });

  api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  });

  return api;
};

// Service instances
export const userService = createApiService('http://localhost:8082');
export const walletService = createApiService('http://localhost:8083');
export const paymentService = createApiService('http://localhost:8084');
export const transactionService = createApiService('http://localhost:8085');
export const commissionService = createApiService('http://localhost:8086');

// API methods
export const apiService = {
  // User APIs
  getUser: (userId) => userService.get(`/users/${userId}`),
  updateUser: (userId, data) => userService.put(`/users/${userId}`, data),
  getAllUsers: () => userService.get('/users'),

  // Wallet APIs
  getWallet: (userId) => walletService.get(`/wallets/${userId}`),
  creditWallet: (data) => walletService.post('/wallets/credit', data),
  debitWallet: (data) => walletService.post('/wallets/debit', data),

  // Payment APIs
  createOrder: (data) => paymentService.post('/payments/create-order', data),
  getOrder: (orderId) => paymentService.get(`/payments/order/${orderId}`),

  // Transaction APIs
  initiateTransaction: (data) => transactionService.post('/transactions/initiate', data),
  completeTransaction: (transactionId, paymentId) => 
    transactionService.post(`/transactions/complete/${transactionId}?razorpayPaymentId=${paymentId}`),
  getTransaction: (transactionId) => transactionService.get(`/transactions/${transactionId}`),
  getUserTransactions: (userId) => transactionService.get(`/transactions/user/${userId}`),
  getUserTransactionsByMonth: (userId, year, month) => 
    transactionService.get(`/transactions/user/${userId}/month/${year}/${month}`),
  getUserTransactionsByWeek: (userId, year, week) => 
    transactionService.get(`/transactions/user/${userId}/week/${year}/${week}`),

  // Commission APIs
  getUserCommission: (userId) => commissionService.get(`/commission/user/${userId}`),
  updateCommission: (userId, data) => commissionService.put(`/commission/user/${userId}`, data),
  getDefaultCommission: () => commissionService.get('/commission/default'),
  updateDefaultCommission: (data) => commissionService.put('/commission/default', data),
};
