import axios from 'axios';

const USER_SERVICE_URL = 'http://localhost:8082'; // UserService port
const AUTH_SERVICE_URL = 'http://localhost:8081'; // AuthService port

const userApi = axios.create({
  baseURL: USER_SERVICE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

const authApi = axios.create({
  baseURL: AUTH_SERVICE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
[userApi, authApi].forEach(api => {
  api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  });
});

export const authService = {
  login: async (credentials) => {
    // Use UserService for authentication
    const response = await userApi.get('/users/login', {
      params: {
        email: credentials.username, // assuming username is email
        password: credentials.password
      }
    });

    // Mock token generation for now
    const mockToken = 'mock-jwt-token-' + Date.now();
    return {
      data: {
        token: mockToken,
        user: response.data
      }
    };
  },
  signup: (userData) => userApi.post('/users/register', {
    username: userData.username,
    email: userData.email,
    phone: userData.phone,
    password: userData.password,
    role: userData.role
  }),
  refreshToken: () => authApi.post('/auth/refresh'),
};
